mapper.update:
UPDATE NOTICE 
	SET NOTICE_TITLE = #{notice_title}, NOTICE_CONTENT = #{notice_content}
	WHERE NOTICE_NO = #{notice_no}

mapper.select:
SELECT NOTICE_NO, NOTICE_TITLE, TO_CHAR(NOTICE_REG_DT,'YYYY-MM-DD') AS NOTICE_REG_DT,NOTICE_CHK,EMP_NO,NOTICE_CNT FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,NOTICE_CHK,EMP_NO,NOTICE_CNT FROM NOTICE
WHERE LOWER(NOTICE_TITLE) LIKE LOWER('%'||#{searchKeyword}||'%')
ORDER BY NOTICE_NO DESC ) A ) WHERE RN BETWEEN #{start} AND #{last}

SELECT NVL(COUNT(*),0) FROM NOTICE WHERE 1=1
AND NOTICE_CHK =#{notice_chk}
AND LOWER(NOTICE_TITLE) LIKE LOWER('%'||#{searchKeyword}||'%')

SELECT NOTICE_NO, NOTICE_TITLE, TO_CHAR(NOTICE_REG_DT,'YYYY-MM-DD') AS NOTICE_REG_DT,NOTICE_CHK,EMP_NO,NOTICE_CNT
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_REG_DT,NOTICE_CHK,EMP_NO,NOTICE_CNT
					FROM NOTICE 
					WHERE 1=1

						 AND  NOTICE_CHK =#{notice_chk}


						 AND LOWER(NOTICE_TITLE) LIKE LOWER('%'||#{searchKeyword}||'%')

					ORDER BY NOTICE_NO DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{last}
